void menu_setIndex(unsigned char index){
	if(index >= 0 && index < MENU_ITEM_COUNT){
		current_index = index;
	}
}

void menu_next(){
	if(current_index < MENU_ITEM_COUNT-1){
		current_index++;
	}
	menu_print();
}

void menu_prev(){
	if(current_index > 0){
		current_index--;
	}
	menu_print();
}

unsigned char menu_isSelected(){
	return menu_selected;
}

unsigned char menu_select(){
	menu_selected = 1;
	return current_index;
}


